export default function EngagementModels({ isMobile }: any) {
  const router = useRouter();
  const [loading, setLoading] = useState(false);

  const handleClickEngagement = (path: string, e: any) => {
    if (e.ctrlKey || e.metaKey || e.shiftKey || e.button === 1) {
      return;
    }

    e.preventDefault();

    setLoading(true);
    router.push(path);
    setTimeout(() => {
      setLoading(false);
    }, 2000);
  };

  return (
    <div>
      {" "}
      <div className={`flex ${isMobile ? "flex-col" : "flex-row"} gap-[40px]`}>
        <div className="lg:w-[calc(25%-20px)] w-full">
          <div className="bg-[#14CCC3] px-3 py-1 mb-7 lg:w-max w-full">
            <p className="font-bold text-[#fff]  text-base ">
              Your Engineering Innovation Partner
            </p>
          </div>
          <div className="border-l-[1px] border-[#14CCC3] ">
            <p className="pl-3 leading-[105%] text-base font-[300] text-[#1D1D1F]">
              We are a technology partner helping early-stage start-ups, large
              enterprises, and Fortune 100 companies with technology development
              outsourcing, all conveniently and cost effectively.
            </p>
          </div>
        </div>

        <div className="lg:border-r-[2px] border-b-[2px] border-[#14CCC3]"></div>
        <div className={`${isMobile ? "w-full" : "w-[calc(75%-20px)]"}`}>
          <p className="text-[#1D1D1F] font-semibold text-base">
            Engagement Models
          </p>
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-[24px] mt-4">
            {cardDataff.map((card: any, index: any) => (
              <a
                onClick={(e) => handleClickEngagement("/engagement-model", e)}
                href="/engagement-model"
                key={index}
              >
                <div
                  key={index}
                  style={{ boxShadow: "1px 1px 24px 0px #00000014" }}
                  className="w-full cursor-pointer rounded-[8px] bg-white lg:h-[190px] h-auto group hover:bg-gradient-to-bl hover:from-[#A5E3DE3D] hover:to-[#0D9DA2] transition-colors duration-300"
                >
                  <div className="flex items-center pt-3">
                    <div className="border-b-[0.25px] border-[#14CCC3] w-[28px] group-hover:border-white transition-colors duration-300"></div>
                    <div className="group">
                      <div className="transition duration-300 group-hover:brightness-0 group-hover:invert mx-3 h-[57px] w-[52px]">
                        {card.image}
                      </div>
                    </div>
                    <div className="border-b-[0.25px] border-[#14CCC3] w-full group-hover:border-white transition-colors duration-300"></div>
                  </div>
                  <div className="p-4 flex items-center justify-between">
                    <div className="w-[70%]">
                      <p className="text-[#1D1D1F] font-semibold text-base group-hover:text-white transition-colors duration-300">
                        {card.title}
                      </p>
                      <p className="text-[#1D1D1F] font-normal text-base group-hover:text-white transition-colors duration-300">
                        {card.description}
                      </p>
                    </div>
                    <div className="w-[32px] h-[32px] bg-[#14CCC3] rounded-full flex items-center justify-center">
                      <IoIosArrowForward color="#fff" />
                    </div>
                  </div>
                </div>
              </a>
            ))}
          </div>
        </div>
      </div>{" "}
      {loading && (
        <>
          <div
            style={{ zIndex: "1111" }}
            className="fixed left-0  top-0 bg-black/70  w-full h-screen flex justify-center items-center"
          >
            <CustomLoader />
          </div>
        </>
      )}
    </div>
  );
}
